INSERT INTO `libros` (`id_libro`, `id_categoria`, `nombre_libro`, `editorial`, `autor`, `precio`, `cantidad`, `resumen`, `imagen`) VALUES (NULL, '5', 'Caracterizaci�n de residuos industriales', 'IC Editorial', 'Sonia Olvera Lobo', '15.000', '52', '- Caracterizar los residuos industriales susceptibles de ser tratados en planta o vertidos en dep�sitos de seguridad. Explicar los procedimientos t�cnicos y protocolos de caracterizaci�n para los diferentes tipos de residuos industriales.Ebook ajustado al certificado de profesionalidad Gesti�n de residuos urbanos e industriales: UC0077_2, MF0077_2, UF0288', 'libros/6.jpg');
